import React, { Component } from "react";

class Image extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            image: null
        };
    }
    componentDidMount() {
        fetch("/images/image")
            .then(res => res.json(), onerror)
            .then(json => {
                this.setState({
                    isLoaded: true,
                    image: json
                });
            });
    }
    render() {
        const { error, isLoaded, image } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            // return <img src={image.url} />;
            console.log(image);
        }
    }
}
export default Image;
